//import logo200Image from 'assets/img/logo/logo_200.png';
import logo200Image from 'assets/img/logo/logo.png';
import sidebarBgImage from 'assets/img/sidebar/sidebar-1.jpg';
//import SourceLink from 'components/SourceLink';
import React from 'react';
import { connect } from 'react-redux';
import 'react-perfect-scrollbar/dist/css/styles.css';
import PerfectScrollbar from 'react-perfect-scrollbar'

import {
  MdPlayArrow,
  MdKeyboardArrowDown,
} from 'react-icons/md';
import { Link,NavLink } from 'react-router-dom';

import {
  Collapse,
  Nav,
  Navbar,
  NavItem,
  NavLink as BSNavLink,
} from 'reactstrap';
import bn from 'utils/bemnames';


const navAdmin = [
  { to: '/allusers', name: 'Admins', exact: true, Icon: MdPlayArrow },
];


const allItems =[
  { to: '/allChildren', name: 'Clients1', exact: true, Icon: MdPlayArrow },
  //{ to: '/Allparents', name: 'Parents', exact: true, Icon: MdPlayArrow },
  { to:'/allmentors',name:"Mentors",exact:true,Icon:MdPlayArrow},
  { to:'/programs',name:"Programs",exact:true,Icon:MdPlayArrow},
  { to:'/allStudentTodos',name:"Habits",exact:true,Icon:MdPlayArrow},

]

const allAfterTodo = [
  { to:'/resources',name:"Resources",exact:true,Icon:MdPlayArrow},
  { to:'/events',name:"Events",exact:true,Icon:MdPlayArrow},
]



const allAfterHelp = [
  { to:'/cms',name:"Cms",exact:true,Icon:MdPlayArrow},

]
const allAfterCms = [
  { to:'/thermoques',name:"Thermometer Questions",exact:true,Icon:MdPlayArrow},
  { to:'/languages',name:"Languages",exact:true,Icon:MdPlayArrow},
]
const navItems = [
  { to: '/dashboard', name: 'dashboard', exact: true, Icon: MdPlayArrow },  
];
const allHelp = [
  { to:'/Allhelpsuggestions',name:"Help Sugesstions",exact:true,Icon:MdPlayArrow},
  { to:'/helpQuestions',name:"Help",exact:true,Icon:MdPlayArrow},

]

// const allTodos = [
//   //{ to:'/allParentTodos',name:"Parent Habits",exact:true,Icon:MdPlayArrow},
//   { to:'/allMentorTodos',name:"Mentor Habits",exact:true,Icon:MdPlayArrow},
// ]
const bem = bn.create('sidebar');

class Sidebar extends React.Component {
	 constructor(props){
    super(props);
    //console.log(this.props.auth.user.user_type);
  this.state = {
    isOpenComponents: true,
    isOpenContents: true,
    isOpenPages: false, 
  };
}

  handleClick = name => () => {
    this.setState(prevState => {
      const isOpen = prevState[`isOpen${name}`];

      return {
        [`isOpen${name}`]: !isOpen,
      };
    });
  };

  render() {
    let ActiveSideBar;
    ActiveSideBar=  <Nav vertical>
    {navItems.map(({ to, name, exact, Icon, Class }, index) => (
      <NavItem key={index} className={bem.e('nav-item')}>
        <BSNavLink
          id={`navItem-${name}-${index}`}
          className={Class}
          tag={NavLink}
          to={to}
          activeClassName="active"
          exact={exact}>
          <Icon className={bem.e('nav-item-icon')} />
          <span className="">{name}</span>
        </BSNavLink>
      </NavItem>
    ))}


    {/* <Collapse isOpen={this.state.isOpenContents}> */}
      {allItems.map(({ to, name, exact, Icon, Class }, index) => (
        <NavItem key={index} className={bem.e('nav-item')}>
          <BSNavLink
            id={`navItem-${name}-${index}`}
            className={Class}
            tag={NavLink}
            to={to}
            activeClassName="active"
            exact={exact}>
            <Icon className={bem.e('nav-item-icon')} />
            <span className="">{name}</span>
          </BSNavLink>
        </NavItem>
      ))}
    {/* </Collapse> */}
    {/* <NavItem
        className={bem.e('nav-item')}
        onClick={this.handleClick('TodosPages')}>
        <BSNavLink className={bem.e('nav-item-collapse')}>
          <div className="d-flex">
            <MdPlayArrow className={bem.e('nav-item-icon')} />
            <span className="">Habits</span>
          </div>
          <MdKeyboardArrowDown
            className={bem.e('nav-item-icon')}
            style={{
              padding: 0,
              transform: this.state.isOpenTodosPages
                ? 'rotate(0deg)'
                : 'rotate(-90deg)',
              transitionDuration: '0.3s',
              transitionProperty: 'transform',
            }}
          />
        </BSNavLink>
      </NavItem>
       */}
    {/* <Collapse isOpen={this.state.isOpenTodosPages}>
      {allTodos.map(({ to, name, exact, Icon, Class }, index) => (
        <NavItem key={index} className={bem.e('nav-item')}>
          <BSNavLink
            id={`navItem-${name}-${index}`}
            className={Class}
            tag={NavLink}
            to={to}
            activeClassName="active"
            exact={exact}>
            <Icon className={bem.e('nav-item-icon')} />
            <span className="">{name}</span>
          </BSNavLink>
        </NavItem>
      ))}
    </Collapse> */}

    {/* <Collapse isOpen={this.state.isOpenContents}> */}
      {allAfterTodo.map(({ to, name, exact, Icon, Class }, index) => (
        <NavItem key={index} className={bem.e('nav-item')}>
          <BSNavLink
            id={`navItem-${name}-${index}`}
            className={Class}
            tag={NavLink}
            to={to}
            activeClassName="active"
