import React from 'react';
import AppConstants from 'AppConstants';
import axios from 'axios';
import Page from 'components/Page';
import {
    Row,
    Col,
    Card,
    CardBody,
    Form,
    FormGroup,
    Label,
    Input,
    Button,
  } from 'reactstrap';
import { Link } from 'react-router-dom';
import Loading from 'assets/img/loader.gif';
import {ReactTitle} from 'react-meta-tags';
import DatePicker from "react-datepicker";
import { Multiselect } from 'multiselect-react-dropdown';
import Switch from "react-switch";
import placeholderimg from '../../assets/img/placeholder.png';
import {
  MdFileUpload, MdDateRange
} from 'react-icons/md';
import moment from "moment";

var prg1 = [];
var prg2 = [];
var rem = [];
class EditEvent extends React.Component{
    constructor(){
        super();
        this.state = {
            title: '',
            description: '',
            image:'',
            errors:{},
            errMsg : '',
            state:'',
            city:'',
            zipcode:'',
            status:true, 
            address:'',
            old_img:'',
            prev_logo: '',
            view_logo: '',
            programs:[],
            profile_image:'',
            seletedprogram : [],
            // selectedValue:[],
            title_error : false,
            description_error : false,
            address1_error:false,
            address2_error:false,
            city_error:false,
            state_error:false,
            zipcode_error:false,
            status_error:false,
            dob_error:false,
            prog_error:false,
            dateofevent:'',
            endDate:'',
            event_link:'',
        }
        this.handleSubmit = this.handleSubmit.bind(this);  
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleInputFileChange = this.handleInputFileChange.bind(this);
        this.onSelect = this.onSelect.bind(this);
        this.onRemove = this.onRemove.bind(this);
        this.handledateOfEvent = this.handledateOfEvent.bind(this);
        this.handleprofile_image = this.handleprofile_image.bind(this);
        this.handleEndDate = this.handleEndDate.bind(this);
        this.onChangeNumberkey = this.onChangeNumberkey.bind(this);


    }
    onChangeNumberkey(e){
      const states = this.state
      const re = /^[0-9\b]+$/;
      if (e.target.value === '' || re.test(e.target.value)) {
      states[e.target.name] = e.target.value;
      this.setState({states})
      }
    }
    handleEndDate(bday){
      this.setState({endDate:bday})
    }
    openDatepicker1 = () => this._calendar1.setOpen(true);
    openDatepicker2 = () => this._calendar2.setOpen(true);

    handleprofile_image(e) {
        this.setState({
      [e.target.name]:e.target.files[0],

      prev_logo:URL.createObjectURL(e.target.files[0])

    });
  }
    handleInputChange(e) {
      if(e.target.name == "title"){
        if(e.target.value != ""){
          this.setState({title_error:false})
        }else{
          this.setState({title_error:true})
        }
      }
      if(e.target.name == "description"){
        if(e.target.value != ""){
          this.setState({description_error:false})
        }else{
          this.setState({description_error:true})
        }
      }
      if(e.target.name == "address1"){
        if(e.target.value != ""){
          this.setState({address1_error:false})
        }else{
          this.setState({address1_error:true})
        }
      }
      if(e.target.name == "address2"){
        if(e.target.value != ""){
          this.setState({address2_error:false})
        }else{
          this.setState({address2_error:true})
        }
      }
      if(e.target.name == "city"){
        if(e.target.value != ""){
          this.setState({city_error:false})
        }else{
          this.setState({city_error:true})
        }
      }
      if(e.target.name == "state"){
        if(e.target.value != ""){
          this.setState({state_error:false})
        }else{
          this.setState({state_error:true})
        }
      }
      if(e.target.name == "zipcode"){
        if(e.target.value != ""){
          this.setState({zipcode_error:false})
        }else{
          this.setState({zipcode_error:true})
        }
      }

        this.setState({
            [e.target.name]: e.target.value.trimLeft(/(^\s+|\s+$)/g, '')
        });
    }

    handleInputFileChange(e) {
        this.setState({
            [e.target.name]: e.target.files[0]       
        });
    }
    onSelect(selectedList, selectedItem) {
    //   if(selectedList != ""){
    //     this.setState({prog_error:false})
    // }
    //   var arr = selectedList;
    //   var array = []
    //   arr.map((result,i)=>{
    //     array.push(result.name);
    //   })
      this.setState({
        selectedValue: selectedList
      });
    }

    onRemove(selectedList, removedItem) {
      if(selectedList == ""){
        this.setState({prog_error:true})
    }
    }
    handledateOfEvent(bday){
      if(bday != ""){
          this.setState({dob_error:false})
      }else{
        this.setState({dob_error:true})
      }
      this.setState({dateofevent:bday})
  }
  handleStatusChange(checked){
    this.setState({status : checked})
  }
    handleValidation(){
        let errors = {};
        let formIsValid = true;
        console.log("this.state.profile_image",this.state.profile_image)
        if(this.state.profile_image && this.state.profile_image.name && !this.state.profile_image.name.match(/.(jpg|jpeg|png)$/i)){
          formIsValid = false;
          errors["profile_image"] = "Please select valid image (Ex: jpg | jpeg | png )";
          }
        if(this.state.profile_image === ''){
          formIsValid = false;
          errors["profile_image"] = "Please choose profile image";
        }
        if(!this.state.title) {
            formIsValid = false;
            errors["title"] = "Please enter title";
        }
        if(!this.state.description){
            formIsValid = false;
            errors["description"] = "Please enter description";
        }

          if(!this.state.address1){
            formIsValid = false;
            errors["address1"] = "Please enter address";
          }

          if(!this.state.city){
            formIsValid = false;
            errors["city"] = "Please enter city";
          }
          if(!this.state.state){
            formIsValid = false;
            errors["state"] = "Please select state";
          }
          if(!this.state.zipcode){
            formIsValid = false;
            errors["zipcode"] = "Please enter zipcode";
          }
          if(this.state.seletedprogram.length == '' &&  this.state.selectedValue == ''){
            formIsValid = false;
            errors["seletedprogram"] = "Please select program";
          }
          if(!this.state.dateofevent){
            formIsValid = false;
            errors["dateofevent"] = "Please select date of event";
          }

          if(!this.state.endDate){
            formIsValid = false;
            errors["endDate"] = "Please select end date";
          }
          if(this.state.event_link && !/^https?:\/\//i.test(this.state.event_link)){
            formIsValid = false;
            errors["url"] = "Please enter valid URL";
          }

          if(this.state.endDate!='' && this.state.dateofevent!=''){
            if(new Date(this.state.endDate).toISOString() <= new Date(this.state.dateofevent).toISOString()){
              console.log("Coming");
              formIsValid = false;
              errors["date"] = "End date should be greater than Start date";
            }
          }
        // if (this.state.image && !this.state.image.name.match(/.(jpg|jpeg|png|gif)$/i)){
        //     formIsValid = false;
        //     errors["image"] = "Please select valid image (Ex: jpg | jpeg | png | gif)";
        // }
        this.setState({
            errors: errors,
            title_error : true,
            description_error:true,
            address1_error:true,
            address2_error:true,
            city_error:true,
            state_error:true,
            zipcode_error:true,
            dob_error:true,
            prog_error:true
          });
        return formIsValid;
    }
    handleSubmit = event => {
        event.preventDefault();
        var prgs = []
        console.log(" this.state.selectedValue", this.state.selectedValue)
        this.state.selectedValue.map((prg,index)=>{
          prgs.push(prg._id)
      })
        if(this.handleValidation()){
            this.setState({loading:1});
            let formData = new FormData();

            formData.append('id', this.props.match.params.id);
            formData.append('profile_image', this.state.profile_image);
            formData.append('old_img', this.state.old_img);  
            formData.append('title', this.state.title);
            formData.append('description', this.state.description);
            formData.append('address1', this.state.address1);
            formData.append('address2', this.state.address2);
            formData.append('zipcode', this.state.zipcode);
            formData.append('state', this.state.state);
            formData.append('city', this.state.city);
            formData.append('status', this.state.status);
            formData.append('program', JSON.stringify(prgs));
            formData.append('dateofevent', this.state.dateofevent);
            formData.append('endDate', this.state.endDate);
            formData.append('event_link', this.state.event_link);
            axios.post(AppConstants.API+'/events/updateEvent',formData)
                .then(res => {   
                if(res.data.Status == "Success"){
                    this.setState({loading:''});
                    setTimeout((e) => {            
                        this.props.history.push('/events');
                    }, 1500);
                    this.setState({
                        errMsg: (<div className="alert alert-success fade show mt-3">{res.data.msg}</div>)
                    });
                    setTimeout(() => {
                        this.setState({
                        errMsg: false,
                        });
                    }, 1000);
                }
                else{
                this.setState({loading:''});
                this.setState({
                errMsg: (<div className="alert alert-danger fade show mt-3">{res.data.msg}</div>)
                });
                }
            });
        } 
      };  
    componentDidMount(){
        let prg1 = [];
        let prg2 = [];
        var element = document.getElementById("navItem-Events-1");
        element.classList.add("active");
        var that = this;
        fetch(AppConstants.API+'/programs/getAllactiveprograms').then(res=>{
            if(res.status == 200){
                return res.json();
            }
          }).then(result=>{
            console.log(result.data,"result.data")
            result.data.map((res,i)=>{
              var prg = {}
              prg["name"] = res.title
              prg["_id"] = res._id
              prg1.push(prg)
            })
            this.setState({programs:prg1})
          })
        fetch(AppConstants.API+'/events/getEventData/'+this.props.match.params.id)
        .then(function(res) {
            if(res.status == "200"){
                return res.json();
            }
        })
        .then(function(data) {
          console.log("datdata",data)
            if(data.Status == "Success"){
                // var dob = moment(data.data[0].dateofevent).format('MM-DD-YYYY');
                // console.log("dob",dob);
                // data.data[0].program.map((res,i)=>{
                //     var prg = {};
                //     prg["name"] = res
                //     prg2.push(prg)
                // })
                console.log("data.data.program",data.data.program)
                that.setState({
                    title: data.data.title,
                    //description: data.data.description,
                    //programs: data.data.program,
                    selectedValue : data.data.program,
                    description: data.data.description,  
                    address1: data.data.address1, 
                    address2: data.data.address2,  
                    city: data.data.city,          
                    state: data.data.state,  
                    zipcode: data.data.zipcode,  
                    dateofevent: new Date(data.data.dateofevent), 
                    status:data.data.status ,
                    view_logo:data.data.profile_image,
                    old_img:data.data.profile_image,
                    profile_image:data.data.profile_image,
                    event_link:data.data.event_link,
                    endDate: new Date(data.data.endDate),
                }); 
            }
            else
            {
            }
        }); 
    }
    render(){
      let image;
      if(this.state.profile_image == '')
      {
        image = placeholderimg;
      }else if(this.state.prev_logo == ''){
        image = this.state.profile_image;
      } else{
        image = this.state.prev_logo;
      }
        return(
            <Page title="Edit Event" /* breadcrumbs={[{ name: 'Add Ask Assistance', active: true }]} */>
            <ReactTitle title="Edit Event"/>
            <ul className="cst-breadcrumb">
                <li><Link to={'/dashboard'}>Home</Link> </li>
                <li>  <Link to={'/events'}>  Events </Link> </li>
                <li> Edit Event</li>
            </ul>
            <Row>
                <Col xl={12} lg={12} md={12}>
                <Card>
                    <CardBody>
                    <Form name="changeadminpassword" onSubmit= { this.handleSubmit }>
                    <FormGroup row>
                        <Label for="program" sm={2}>
                            Program  <span>*</span>
                        </Label>
                        <Col sm={10}>
                            <div>
                              <Multiselect
                                options={this.state.programs} // Options to display in the dropdown
                                selectedValues={this.state.selectedValue} // Preselected value to persist in dropdown
                                onSelect={this.onSelect} // Function will trigger on select event
                                onRemove={this.onRemove} // Function will trigger on remove event
                                displayValue="name" // Property name to display in the dropdown options
                                placeholder="Program"
                                />
                            </div>
                            {this.state.prog_error == true ? <span className="error" style={{color: 'red'}}>{this.state.errors["seletedprogram"]}</span> :''}
                        </Col>
                    </FormGroup>
                        <FormGroup row>
                            <Label for="title" sm={2}>
                            Title <span style={{color: 'red'}}> * </span>
                            </Label>
                            <Col sm={10}>
                                <Input
                                type="text"
                                name="title"
                                placeholder="Title"                      
                                value={this.state.title}
                                onChange={ this.handleInputChange }                  
                                />
                                {this.state.title_error == true ? <span className="error" style={{color: 'red'}}>{this.state.errors["title"]}</span> : ""}
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                        <Label for="description" sm={2}>
                        Description <span style={{color: 'red'}}> * </span>
                        </Label>
                        <Col sm={10}>
                            <Input
                                type="textarea"
                                name="description"
                                placeholder="Description"                      
                                value={this.state.description}
                                onChange={ this.handleInputChange }                  
                            />
                            {this.state.description_error == true ? <span className="error" style={{color: 'red'}}>{this.state.errors["description"]}</span>:""}
                        </Col>
                        </FormGroup>
                        <FormGroup row>
                        <Label for="dob" sm={2}>
                            Date of Event <span>*</span>
                        </Label>
