import React from 'react';
import {
  Row,
  Col,
  Card,
  CardHeader,
  CardBody,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  FormFeedback,
} from 'reactstrap';
import Page from 'components/Page';
import axios from 'axios';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import NumberFormat from 'react-number-format';
import {ReactTitle} from 'react-meta-tags';
import  { Link } from 'react-router-dom';
import AppConstants from 'AppConstants';
import placeholderimg from './../assets/img/placeholder.png';
import { MdInfoOutline} from 'react-icons/md';

class ViewadminProfile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      _id: this.props.auth.user._id,
      firstname: '',
      lastname: '',
      email: '',
      profile_img: '',
      address1: '',
      phone: '',
      gender: '',
      errMsg: '',
      address2: '',
      country: '',
      state: '',
      zipcode: '',
      city: ''
    }
    this.inputChangedHandlerContact = this.inputChangedHandlerContact.bind(this);
  }
  inputChangedHandlerContact = (values) => {
    this.setState({
      phone: values.value,
    });
  }
  componentDidMount() {
    const that = this;
    fetch(AppConstants.API + '/users/profile/' + this.props.auth.user._id)
      .then(function (res) {
        if (res.status == 200) {
          return res.json();
        }
      })
      .then(function (data) {
        console.log(data)
        if (data.Status == "Success") {
          that.setState({
            firstname: data.data.firstname,
            lastname: data.data.lastname,
            email: data.data.email,
            profile_image: data.data.profile_image,
            address1: data.data.address1,
            phone: data.data.phone,
            gender: data.data.gender,
            country: data.data.country,
            state: data.data.state,
            address2: data.data.address2,
            city: data.data.city,
            zipcode: data.data.zipcode,
          });
        } else {
        }
      });
  }



  render() {
    const { user } = this.props.auth;
    const { phone } = this.state;
    let image;
    if (this.state.profile_image == '') {
      image = placeholderimg;
    }
    else {
      image = this.state.profile_image;
    }
    return (
      <Page title="View Profile">
          <ul className="cst-breadcrumb">
              <li><Link to={'/dashboard'}>Home</Link></li>
              <li> View Profile</li>
          </ul>
        <ReactTitle title="View Profile"/>
