                        <label className="col-auto col-form-label">{this.state.firstname ? this.state.firstname : 'N/A'}</label>
                      </div>

                      <div className="row form-group">
                        <label for="exampleEmail" className="col-4 col-form-label">Last Name</label>
                        <label className="col-auto col-form-label">{this.state.lastname ? this.state.lastname : 'N/A'}</label>
                      </div>

                      <div className="row form-group">
                        <label for="exampleEmail" className="col-4 col-form-label">Email</label>
                        <label className="col-auto col-form-label"> {this.state.email}</label>
