        <Row>
          <Col xl={12} lg={12} md={12}>
            <Card className="pt-0">
            <CardHeader> 
              <Label for="examplePassword" sm={12} className="px-0"><strong><MdInfoOutline fontSize="24" className="mr-2" />Profile Info</strong>
              </Label>
              </CardHeader>
              <CardBody>
                <Form name="viewadminprofile">
                  <FormGroup row>

                   <Col className="col-2">
                      <figure className="view-profile-img" ><img src={image} /></figure>
                    </Col>


                    <Col sm={7} className="profile-info ml-3" >

                      <div className="row form-group">

                        <h2 className="cst-pfname mb-0">
                        {this.state.firstname ? this.state.firstname : 'N/A'} {this.state.lastname ? this.state.lastname : 'N/A'}</h2>
                      </div>


                      <div className="row form-group">
                        <label htmlFor="exampleEmail" className="col-4 col-form-label">Email</label>
                        <label className="col-auto col-form-label"> {this.state.email}</label>
                      </div>

                      <div className="row form-group">
                        <label htmlFor="exampleEmail" className="col-4 col-form-label"> Contact Number</label>
                        <label className="col-auto col-form-label">{phone ? phone : 'N/A'}</label>
                      </div>

                      <div className="row form-group">
                        <label htmlFor="exampleEmail" className="col-4 col-form-label">Address</label>
                        <label className="col-auto col-form-label"> {this.state.address1 ? this.state.address1 : 'N/A'}</label>
                      </div>

                      <div className="row form-group">
                        <label htmlFor="exampleEmail" className="col-4 col-form-label">City</label>
                        <label className="col-auto col-form-label">{this.state.city ? this.state.city : 'N/A'}</label>
                      </div>


                      <div className="row form-group">
                        <label htmlFor="exampleEmail" className="col-4 col-form-label">State</label>
                        <label className="col-auto col-form-label"> {this.state.state ? this.state.state : 'N/A'}</label>
                      </div>



                      <div className="row form-group">
                        <label htmlFor="exampleEmail" className="col-4 col-form-label">Zip Code</label>
                        <label className="col-auto col-form-label"> {this.state.zipcode ? this.state.zipcode : 'N/A'}</label>
                      </div>


                    </Col>


                  </FormGroup>
                </Form>
              </CardBody>

            </Card>
          </Col>
        </Row>




      </Page>
    );
  };
}

ViewadminProfile.propTypes = {
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors
});

//export default ChangepasswordPage;
export default connect(mapStateToProps, {})(ViewadminProfile);
