    Row,
    Col,
    Card,
    CardBody,
    Button,
    Form,
    FormGroup,
    Label,
    Input,
  } from 'reactstrap';
import Loading from 'assets/img/loader.gif';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import ReactDOM from 'react-dom';
import Switch from "react-switch";
import NumberFormat from 'react-number-format';

class AddProgram extends React.Component{
    constructor(){
        super();
        this.state={
            ServiceType : "",
            status : true,
            loading : "",
            errors : {},
            ServiceType_error : false,
            MarketPrice:"",
            MarketPrice_error:false,
            MRprice:'',
            MRprice_error:false,
            PremiumPrice:'',
            PremiumPrice_error:false,
            PRprice:'',
            PRprice_error:false,
            ID:'',
            ID_error:false,
        }
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }
    handleInputChange(e){
        if(e.target.name == "ServiceType"){
            if(e.target.value != ""){
                this.setState({ServiceType_error:false})
            }else{
                this.setState({ServiceType_error:true})
            }
        }
        if(e.target.name == "description"){
            if(e.target.value != ""){
                this.setState({description_error:false})
            }else{
                this.setState({description_error:true})
            }
        }
        if(e.target.name == "duration"){
            if(e.target.value != 0){
                this.setState({duration_error:false})
            }else{
                this.setState({duration_error:true})
            }
        }

        this.setState({[e.target.name]:e.target.value})
    }
    handleValidation(){
        let errors = {};
        let formIsValid = true;

        if (!this.state.duration){
            formIsValid = false;
            errors["duration"] = "Please enter duration";
            ReactDOM.findDOMNode(this.refs.duration).focus();
        }
        if (!this.state.description){
            formIsValid = false;
            errors["description"] = "Please enter description";
            ReactDOM.findDOMNode(this.refs.description).focus();
        }
        if (!this.state.ServiceType){
            formIsValid = false;
            errors["ServiceType"] = "Please enter ServiceType";
            ReactDOM.findDOMNode(this.refs.title).focus();
        }
        this.setState({
            errors: errors,
            title_error : true,
            description_error: true,
            duration_error : true
        });
        return formIsValid;
    }
    handleStatusChange(checked){
        this.setState({status : checked})
    }
    handleSubmit(e){
        e.preventDefault();
        let formval={}
        if(this.handleValidation()){
            this.setState({loading:1});
            formval = {
                'title': this.state.title,
                'status': this.state.status,
                'description':this.state.description,
                'duration': this.state.duration
            }
            axios.post(AppConstants.API+'/programs/addPrograms',formval)
                .then(res => { 
                    // console.log(res)
                    if(res.data.Status == "Success"){
                        setTimeout((e) => {
                            this.props.history.push('/programs');
                        }, 2000);
                        setTimeout(() => {
                          this.setState({
                            loading:'',
                            errMsg:  (<div className="alert alert-success fade show mt-3">{res.data.msg}</div>),
                          });
                        }, 1000);
                    }
                    else{
                        setTimeout(() => {
                          this.setState({
                            loading:'',
                            errMsg:  (<div className="alert alert-danger fade show mt-3">{res.data.msg}</div>),
                          });
                        }, 1000);
                    }
            })
        }
        // console.log(this.state.title,this.state.status)
    }
    componentDidMount(){
        var element = document.getElementById("navItem-Services-1");
        element.classList.add("active");
    }
    render(){
