                                            placeholder="ID"                      
                                            value={this.state.ID}
                                            onChange={ this.handleInputChange }   
                                            ref ="ID" className="form-control"  decimalScale={0} allowNegative={false}       
                                            />
                                            {this.state.ID_error == true ? <span className="error" style={{color: 'red'}}>{this.state.errors["ID"]}</span>:""}
                                        </Col>
                                    </FormGroup>


                                    <FormGroup row>
